days, goodDays = int(input()), int(input())distance = [int(input()) for i in range(days)]goodDaysCount = sum(1 for i in distance[2:] if distance[0] <= i <= distance[1])ans = []if goodDaysCount >= goodDays:    print(0)else:    for i in range(2, days):        if distance[i] < distance[0]:            ans.append(distance[0] - distance[i])        elif distance[i] > distance[1]:            ans.append(distance[i] - distance[1])    ans.sort()    print(sum(ans[:goodDays]))